if not frappe.db.exists("User", doc.email):
    try:
        user = frappe.get_doc({
            "doctype": "User",
            "email": doc.email,
            "first_name": doc.full_name,
            "enabled": 1,
            "roles": [{"role": doc.role_type}]
        })
        user.insert(ignore_permissions=True)

        if doc.get("password"):
            user.set_password(doc.password)

        # Optional: Show a message
        # frappe.msgprint(f"User {doc.email} created with role {doc.role_type}.")

    except Exception as e:
        frappe.log_error(f"Error creating user: {str(e)}", "Gym Management User Creation")
else:
    # User already exists â€” do nothing
    pass
