// ✅ 1. Helper function to read browser cookie (fetches logged in user's email)
function getCookieValue(name) {
    const cookies = document.cookie.split('; ');
    for (const cookie of cookies) {
        const [key, value] = cookie.split('=');
        if (key === name) return decodeURIComponent(value);
    }
    return null;
}

// ✅ 2. Get logged in user's email from cookie
const email = getCookieValue('user_id');
console.log('user_id from cookie:', email);

// ✅ 3. Set email field after Web Form is fully loaded
frappe.web_form.after_load = () => {
    console.log("Web Form loaded");

    // Set the email field (must be of type Data, not Link)
    frappe.web_form.set_value("email", email).then(() => {
        console.log("Email field set to:", email);
    });
};

// ✅ 4. Filter trainer_name based on selected class_boooking (specialization)
frappe.web_form.on('class_boooking', function(field, value) {
    console.log("[DEBUG] Specialization selected:", value);

    // Clear existing trainer_name value & options
    frappe.web_form.set_value('trainer_name', '');
    frappe.web_form.fields_dict.trainer_name.df.options = '';
    frappe.web_form.fields_dict.trainer_name.refresh();

    if (!value) return;

    // Fetch trainers by specialization
    frappe.call({
        method: "frappe.client.get_list",
        args: {
            doctype: "TrainerReg",
            fields: ["name1"],
            filters: { specialization: value },
            limit_page_length: 100
        },
        callback: function(r) {
            const trainers = r.message || [];
            if (trainers.length === 0) {
                console.warn('[DEBUG] No trainers found for specialization:', value);
                return;
            }

            const options = trainers.map(t => t.name1).join('\n');
            frappe.web_form.fields_dict.trainer_name.df.options = options;
            frappe.web_form.fields_dict.trainer_name.refresh();
            console.log('[DEBUG] trainer_name options updated:', options);
        },
        error: function(err) {
            console.error('[ERROR] frappe.call failed:', err);
        }
    });
});
