frappe.web_form.after_load = () => {
    console.log("✅ Web Form loaded. Waiting for Workout Plan field...");

    const waitForSelect = setInterval(() => {
        const $select = $('select[data-fieldname="select_workout_plan"]');

        if ($select.length) {
            clearInterval(waitForSelect);
            console.log("🎯 Select Workout Plan field is ready");

            $select.on("change", function () {
                const level = $(this).val();
                console.log("📌 Selected Level:", level);

                const plans = {
                    Beginner: [
                        ["Monday", "Upper Body Strength"],
                        ["Tuesday", "Lower Body + Core"],
                        ["Wednesday", "Mobility / Recovery"],
                        ["Thursday", "Full Body + Cardio"],
                        ["Friday", "Push/Pull Split"],
                        ["Saturday", "Lower Body + Cardio"],
                        ["Sunday", "Stretching / Yoga"],
                    ],
                    Intermediate: [
                        ["Monday", "Chest + Triceps"],
                        ["Tuesday", "Back + Biceps"],
                        ["Wednesday", "Mobility + Core"],
                        ["Thursday", "Legs + Isolation"],
                        ["Friday", "Shoulders + HIIT"],
                        ["Saturday", "Cardio Circuits"],
                        ["Sunday", "Yoga / Recovery"],
                    ],
                    Advanced: [
                        ["Monday", "Heavy Push (Chest/Shoulders)"],
                        ["Tuesday", "Heavy Pull (Back/Biceps)"],
                        ["Wednesday", "Mobility + Core Stability"],
                        ["Thursday", "Legs + Plyometrics"],
                        ["Friday", "Push/Pull Split + Volume"],
                        ["Saturday", "HIIT, Agility & Power"],
                        ["Sunday", "Deep Stretch / Recovery"],
                    ]
                };

                const selected_plan = plans[level] || [];

                let preview = selected_plan.map(([day, focus]) => `${day}: ${focus}`).join('\n');

                frappe.web_form.set_value("plan_preview", preview);
                console.log("✅ Plan preview updated");
            });
        }
    }, 200);
};
