// ✅ Helper to get logged-in user email from cookie
function getCookieValue(name) {
    const cookies = document.cookie.split('; ');
    for (const cookie of cookies) {
        const [key, value] = cookie.split('=');
        if (key === name) return decodeURIComponent(value);
    }
    return null;
}

// ✅ After the web form loads
frappe.web_form.after_load = () => {
    const email = getCookieValue('user_id');
    console.log('Logged-in user_id (email):', email);

    // Set the email field (make sure it's a Data field)
    frappe.web_form.set_value('email', email).then(() => {
        console.log("Email field set to:", email);
    });
    frappe.web_form.set_df_property('email', 'read_only', 1);

    // Fetch latest trainer from Gym Class Booking for this user
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: 'Gym Class Booking',
            filters: { email: email },
            fields: ['trainer_name'],
            limit_page_length: 1,
            order_by: 'creation desc'
        },
        callback: function(res) {
            const bookings = res.message || [];
            console.log("Gym Class Booking response:", bookings);

            if (bookings.length > 0 && bookings[0].trainer_name) {
                const trainer_name = bookings[0].trainer_name;
                console.log("Trainer name from booking:", trainer_name);
                setTrainerFieldOptions([trainer_name], trainer_name);
            } else {
                setTrainerFieldOptions([], '');
            }
        }
    });
};

// ✅ Utility to set trainer_name field options and value
function setTrainerFieldOptions(options, selected) {
    frappe.web_form.set_df_property('trainer_name', 'options', options);
    frappe.web_form.set_value('trainer_name', selected);

    const field = frappe.web_form.get_field('trainer_name');
    if (field && field.refresh) {
        field.refresh();
    }
}
